mapper.update:

update address
set    default_add = 'N'
WHERE  default_add = 'Y' AND user_no = #{user_no}


update address
set    default_add = 'Y'
WHERE  address_no = #{address_no} AND user_no = #{user_no}

mapper.delete:
	DELETE
	FROM   address
    WHERE  address_no = #{address_no} AND user_no = #{user_no}
