mapper.select:
SELECT order_no, order_name, book_img, order_date, final_price, order_status FROM (select o.order_no, o.order_name, b.book_img, o.order_date, y.final_price, o.order_status, ROW_NUMBER() OVER(PARTITION BY o.order_no ORDER BY o.order_name DESC) AS rn from orders o, purchase p, book b, pay y where p.book_no = b.book_no AND p.order_no = o.order_no AND y.pay_no = o.order_no AND o.user_no = #{user_no} ) WHERE rn = 1 AND
order_date > sysdate-#{search_date}
AND 1 = 1
AND order_status = #{search_status}
order by order_no desc

mapper.update:

		UPDATE book b
		SET    b.stock = (select (b.stock + p.product_count) as stock
                 		  from  purchase p,
                 		  		orders o
                 		  where p.book_no = b.book_no AND
                  	            p.order_no = o.order_no AND                   		   
                 		  		o.order_no = #{order_no}
                 		  )

		WHERE  exists (select 1
                 	   from    purchase p,
                 		  	   orders o
                  	   where   p.book_no = b.book_no AND 
                  	           p.order_no = o.order_no AND      
                 		  	   o.order_no = #{order_no}
               		   )

		UPDATE users u
		SET    user_point = (select (user_point + (use_point - save_point)) as user_point
                  			 from    
                  			 		 pay p
                  			 where   u.user_no = #{user_no} AND 
                  			 		 p.pay_no = #{order_no}
                  			 )

		WHERE  exists (select 1 
         		       from   
         		       		  pay p 
             	       where  u.user_no = #{user_no} AND           	       
             	       		  p.pay_no = #{order_no}
             	      )
